import {Component} from "react";

class HealthBar extends Component{
    constructor(props){
        super(props);
        //basePrice
        //currPrice
        //width
        this.styling = this.styling.bind(this);
        this.state = {
        }

        // //Bind this to its functions
        // this.click = this.click.bind(this)
        // this.mouseOver = this.mouseOver.bind(this)
        // this.mouseOut = this.mouseOut.bind(this)

        // let health = 100
        // let color = "green"
        // let healthBarText = buttonText - this.state.price

    }

    bgColor(percentChange){
        if (percentChange < 0){
            return "red"
        }else{
            return "green"
        }
    }

    styling(base, curr){
        let pctChange = (100*curr-base) / base

        if (netChange > 0){
            //Positive Change
            return{transform: `translate(${this.props.maxWidth}px, 0px)`, height: "50px", width: "260px", backgroundColor: "green"}
        }else{
            //Negative Change
            return{transform: `translate(${this.props.maxWidth}px, 0px)`,height: "50px", width: "50%", backgroundColor: "red"}
        }



    }

    render(){
        return(
            <div>
                <div>{this.props.basePrice}</div>
                <div>{this.props.currPrice}</div>
                <div style={this.styling(this.props.basePrice, this.props.currPrice)}></div>
                <div>Percentage Change: {100*(this.props.currPrice - this.props.basePrice)/this.props.basePrice} %</div>
            </div>
        )

    }
}

export default HealthBar;
