    xhr.open('GET', `${this.url}/v2/account`)

    xhr.setRequestHeader("APCA-API-KEY-ID", this.key_id)
    xhr.setRequestHeader("APCA-API-SECRET-KEY", this.secret_key)

    xhr.send()

